import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";


// import required modules
import { Navigation, Pagination, Mousewheel, Keyboard } from "swiper";

export default function App() {
  return (
    <>
     <div className="container text-center my-5 ">
    <h1 style={{ color: "#e8762b" }}>What our Guests say about us?</h1>
    <h3>Every Review matters</h3>
    <hr />
  </div>
      <Swiper
        cssMode={true}
        navigation={true}
        pagination={true}
        mousewheel={true}
        keyboard={true}
        modules={[Navigation, Pagination, Mousewheel, Keyboard]}
        className="mySwiper"
      >
        <SwiperSlide>
                        A stay at Alpine Riverhill let's you experience Lonavala's natural beauty in an intimate environment. One of Lonavala's most sought-after villas, a stay at Alpine Riverhill promises all the comforts of home and the luxury of a hotel. Located amongst greenery, this grand space is replete with amenities. Here, you can spend your days enjoying the soothing mountain breeze as the on-site staff attend to your needs. As the sun sets, take a stroll by the pool, where you can enjoy mouth-watering grilled tandoori dishes.                    </SwiperSlide>
        <SwiperSlide>Slide 2</SwiperSlide>
        <SwiperSlide>Slide 3</SwiperSlide>
        <SwiperSlide>Slide 4</SwiperSlide>
        <SwiperSlide>Slide 5</SwiperSlide>
        <SwiperSlide>Slide 6</SwiperSlide>
        <SwiperSlide>Slide 7</SwiperSlide>
        <SwiperSlide>Slide 8</SwiperSlide>
        <SwiperSlide>Slide 9</SwiperSlide>
      </Swiper>
    </>
  );
}
